button.event:
local roll = "3d6+" .. (tonumber(sheet.$(field)Mod) or 0)
local rolagem = Firecast.interpretarRolagem(roll)
local mesa = Firecast.getMesaDe(sheet) mesa.activeChat:rolarDados(rolagem, "$(text)");

dataLink.event:
if sheet==nil then return end
local arvore = sheet.arvore
local avanco1 = sheet.avanco1

if arvore == "Escudeiro" then
	self.avanco1.items = {'Cavaleiro','Espadachim','Lanceiro','Pugilista','-'}
elseif arvore == "Arruaceiro" then
	self.avanco1.items = {'Bandido','Ladino','Shinobi','-'}
elseif arvore == "Aprendiz" then
	self.avanco1.items = {'Mago','Elementalista','Necromante','-'}
elseif arvore == "Arqueiro" then
	self.avanco1.items = {'Caçador','Falcoeiro','Arbalaster','-'}
elseif arvore == "Adepto" then
	self.avanco1.items = {'Clerigo','Padre','Inquisitor','-'}
else
	self.avanco1.items = {'-'}
end

if avanco1 ~= nil then
	self.avanco1.text = avanco1
end

if sheet==nil then return end
local avanco1 = sheet.avanco1
local avanco2 = sheet.avanco2

if avanco1 ~= nil then
	self.avanco1.text = avanco1
end

if avanco1 == "Cavaleiro" then
	self.avanco2.items = {'Paladino','-'}
elseif avanco1 == "Espadachim" then
	self.avanco2.items = {'Samurai','-'}
elseif avanco1 == "Lanceiro" then
	self.avanco2.items = {'Matador de Dragão','-'}
elseif avanco1 == "Pugilista" then
	self.avanco2.items = {'Monge','-'}
elseif avanco1 == "Ladino" then
	self.avanco2.items = {'Assassino','Emissário da Morte','-'}
elseif avanco1 == "Mago" then
	self.avanco2.items = {'Invocador','Cronomante','Taoista','-'}
elseif avanco1 == "Necromante" then
	self.avanco2.items = {'Lordes das Sombras','-'}
elseif avanco1 == "Caçador" then
	self.avanco2.items = {'Traper','Wugushi','-'}
elseif avanco1 == "Falcoeiro" then
	self.avanco2.items = {'Domador','-'}
elseif avanco1 == "Clerigo" then
	self.avanco2.items = {'Sacerdote','Cruzado','-'}
elseif avanco1 == "Padre" then
	self.avanco2.items = {'Exorcista','-'}
elseif avanco1 == "Inquisitor" then
	self.avanco2.items = {'Templário','-'}
else
	self.avanco2.items = {'-'}
end

if avanco2 ~= nil then
	self.avanco2.text = avanco2
end
