form.script:
local function roll(field,text)
	local mesa = Firecast.getMesaDe(sheet);

	local mod = math.floor(tonumber(sheet[field]) or 0);
	local rolagem = Firecast.interpretarRolagem("2d6 + " .. mod);

	mesa.activeChat:rolarDados(rolagem, text);
end;

dataLink.event:
