local total = (tonumber(sheet.$(field)Equip) or 0) + (tonumber(sheet.$(field)Add) or 0) + normal;
sheet.$(field)Normal = normal sheet.$(field)Total = total sheet.$(field)Mod = math.floor(total/10)

